import React, { useEffect, useState } from "react";
import "../styles/Admin.css";
import { Button } from "react-bootstrap";
import dataIcon from "../Images/dataset-img.png";
import Instance from "../config/axiosInstance";
import apiURL from "../config/apiURL";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function Admin() {
  const [trainingData, setTrainingData] = useState("");
  const [token, setToken] = useState(null);
  const navigate = useNavigate();
  const notify = () => toast("Data Added Successfully!");
  const submitData = async () => {
    const data = {
      tdata:trainingData 
    }
    try{
      const status = await Instance.post(apiURL.addData, data, {
        headers: {
          Authorization: `Bearer ${token}`, // Include token in the Authorization header
        },
      });
    }
    catch(error){
if( error.status === 401){
  alert("Unaothorized access")
}
    }
    
   
  };

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    } else {
      setToken(localStorage.getItem("token"));
    }
  }, []);

  return (
    <div className="mainContainer">
      <div className="header">
        <img src={dataIcon} className="dataIcon" alt="Data Icon" />
        <p className="title">Data to train AI Model</p>
      </div>
      <textarea
      placeholder="Add Data to train"
        className="customTextarea"
        onChange={(e) => setTrainingData(e.target.value)}
      
      ></textarea>
      <Button
        variant="dark"
        size="sm"
        className="submitButton"
        onClick={submitData}
      >
        Submit
      </Button>
    </div>
  );
}

export default Admin;
