import React, { useState } from "react";
import "../styles/inputComponent.css";
import imageIcon from "../Images/gandhijiImage.png";
import Instance from '../config/axiosInstance';
import apiUrl from '../config/apiURL'
function InputComponent() {
  const [query, setQuery] = useState("");
  const [question, setQuestion] = useState([]);
  const [answer, setAnswer] = useState([])

  const handleQuery = async() =>{
    setQuestion((question) => [...question, query]);
    console.log("This is the question:",question);
    const searchQuery = {
        query: query,
    }
   const answer = await Instance.post(apiUrl.getAnswer,searchQuery )
   console.log(answer);
  }

  return (
    <>
      <div className="main__Container">
        <div className="questionAnswers">


  {question.map}

          <label className="imageQuestions">
            <img src={imageIcon} className="imageIcon" />
            <p className="introWords">
              Hello there, I am Mohandas Karamchand Gandhi, what can i help you
              with?
            </p>
          </label>
          <p className="answer">This is answer</p>
        </div>
      </div>

      <input
        placeholder="Ask questions here?"
        className="questionInput"
        onChange={(e) => {
          setQuery(e.target.value);
        }}
        onKeyDown={(e) =>{
            if( e.key === "Enter"){
                handleQuery();
            }
        }}
      />
    </>
  );
}

export default InputComponent;
